
public class starpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//*
//**
//***
//****
		
		//i is no of rows
		for (int i = 1; i <= 4; i++) {
			
//			and j is no of stars
			for(int j=4;j<=i;j++) {
				System.out.print("*");
			}
//			print on next line
			System.out.println("");
		}
		
	}

}
